package Week1Day1;

public class Primenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=13;
		boolean prime=false;
		for (int i = 2; i < n ; i++) {
			if (i%2==0) {

				prime = true;
				break;
			}
			
		}
		if(prime==!prime)
		{
			System.out.println(prime);

	}
		else
		{
			System.out.println(prime);
		}

}
}
